setTimeout(function() {
  // If bootstrap stylesheet failed to load, use local fallback
  if ($('body').css('color') != 'rgb(51, 51, 51)') {
    $('head').append('<link rel="stylesheet" href="' + d.posts.assets_url + 'bootstrap.min.css"/>');
  }
}, 1000);
// Load dynamic components
$('head style').first()
  .before(d['head_style_loaded']);
$('head style').last()
  .append(d['head_style_inline']);
$('body script').last()
  .before(d['body_script_loaded'])
  .append(d['body_script_inline']);
